import React, { Component } from 'react';
 

// App component - represents the whole app
export default class Login extends Component {

login(){
  var uname=$("#Username").val();
  var pass=$("#Password").val();


  if(User.find({ userName: uname }).fetch()!=""){        
      $("#error-div-no-uname-exists").show();
      setTimeout(function(){ $("#error-div-no-uname-exists").hide(); }, 5000);
  }

}
 
  render() {
    return (
        <body>
        <div className="hed_top">
        
        <div className="mid_container">
        <div className="logoin">Appitude</div>
        </div>
        </div>
              <div className="mid_content">
                <div className="login_col">
                  <div className="card"></div>
                  <div className="card">
                    <h1 className="title">Login</h1>

                    <div id="error-div-no-uname-exists"><span>User name does not exists.</span></div>
                    <div id="error-div-mail"><span>This is not a valid Email address</span></div>

                    <form>
                       
                      <div className="input-container">
                        <input type="text" id="Username" required="required"/>
                        <label for="Username">User Name</label>
                        <div className="bar"></div>
                      </div>
                      <div className="input-container">
                        <input type="password" id="Password" required="required"/>
                        <label for="Password">Password</label>
                        <div className="bar"></div>
                      </div>
                      <div className="button-container">
                        <button onClick={this.login}><span>submit</span></button>
                      </div>
                      <div className="sign"><a href="#">New Registration</a>  <a href="/ForgotPassword">Forget Password</a></div>
                    </form>
                  </div>
                </div>
               </div>
               </body>
              

        
    );
  }
}
